$(document).ready(function () {
  $(".radio-buttons .custom-radio ").click(function () {
    $(this).parent().find(".custom-radio").removeClass("checked");
    $(this).addClass("checked");
  });
});
$(document).ready(function () {
  // Memilih semua elemen radio button dengan nama "template_id"
  const radioButtons = $("input[type='radio'][name='template_id']");

  // Menambahkan event listener untuk setiap elemen radio button
  radioButtons.each(function () {
    $(this).click(function () {
      // Menghapus kelas "checked" dari semua elemen radio button
      radioButtons.removeClass("checked");

      // Menambahkan kelas "checked" pada elemen radio button yang dipilih
      $(this).addClass("checked");
    });
  });
});
